(2, 'wayflyer_bnpl_q4_2021', 'marketing', 'en_US', 'Grow your business with funding through Wayflyer', 'Fast, flexible financing to boost cash flow and help your business grow  one fee, no interest rates, penalties, equity, or personal guarantees. Based on your stores performance, Wayflyer provides funding and analytical insights to invest in your business.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(3, 'wc_shipping_mobile_app_usps_q4_2021', 'marketing', 'en_US', 'Print and manage your shipping labels with WooCommerce Shipping and the WooCommerce Mobile App', 'Save time by printing, purchasing, refunding, and tracking shipping labels generated by <a href=\"https://woocommerce.com/woocommerce-shipping/\">WooCommerce Shipping</a>  all directly from your mobile device!', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(4, 'woocommerce-services', 'info', 'en_US', 'WooCommerce Shipping & Tax', 'WooCommerce Shipping &amp; Tax helps get your store \"ready to sell\" as quickly as possible. You create your products. We take care of tax calculation, payment processing, and shipping label printing! Learn more about the extension that you just installed.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(5, 'your-first-product', 'info', 'en_US', 'Your first product', 'Thats huge! Youre well on your way to building a successful online store  now its time to think about how youll fulfill your orders.<br /><br />Read our shipping guide to learn best practices and options for putting together your shipping strategy. And for WooCommerce stores in the United States, you can print discounted shipping labels via USPS with <a href=\"https://href.li/?https://woocommerce.com/shipping\" target=\"_blank\">WooCommerce Shipping</a>.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(6, 'wc-admin-optimizing-the-checkout-flow', 'info', 'en_US', 'Optimizing the checkout flow', 'Its crucial to get your stores checkout as smooth as possible to avoid losing sales. Lets take a look at how you can optimize the checkout experience for your shoppers.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(7, 'wc-payments-qualitative-feedback', 'info', 'en_US', 'WooCommerce Payments setup - let us know what you think', 'Congrats on enabling WooCommerce Payments for your store. Please share your feedback in this 2 minute survey to help us improve the setup process.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(8, 'share-your-feedback-on-paypal', 'info', 'en_US', 'Share your feedback on PayPal', 'Share your feedback in this 2 minute survey about how we can make the process of accepting payments more useful for your store.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(9, 'google_listings_and_ads_install', 'marketing', 'en_US', 'Drive traffic and sales with Google', 'Reach online shoppers to drive traffic and sales for your store by showcasing products across Google, for free or with ads.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(10, 'wc-subscriptions-security-update-3-0-15', 'info', 'en_US', 'WooCommerce Subscriptions security update!', 'We recently released an important security update to WooCommerce Subscriptions. To ensure your sites data is protected, please upgrade <strong>WooCommerce Subscriptions to version 3.0.15</strong> or later.<br /><br />Click the button below to view and update to the latest Subscriptions version, or log in to <a href=\"https://woocommerce.com/my-dashboard\">WooCommerce.com Dashboard</a> and navigate to your <strong>Downloads</strong> page.<br /><br />We recommend always using the latest version of WooCommerce Subscriptions, and other software running on your site, to ensure maximum security.<br /><br />If you have any questions we are here to help  just <a href=\"https://woocommerce.com/my-account/create-a-ticket/\">open a ticket</a>.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(11, 'woocommerce-core-update-5-4-0', 'info', 'en_US', 'Update to WooCommerce 5.4.1 now', 'WooCommerce 5.4.1 addresses a checkout issue discovered in WooCommerce 5.4. We recommend upgrading to WooCommerce 5.4.1 as soon as possible.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(12, 'wcpay-promo-2020-11', 'marketing', 'en_US', 'wcpay-promo-2020-11', 'wcpay-promo-2020-11', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(13, 'wcpay-promo-2020-12', 'marketing', 'en_US', 'wcpay-promo-2020-12', 'wcpay-promo-2020-12', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(14, 'ppxo-pps-upgrade-paypal-payments-1', 'info', 'en_US', 'Get the latest PayPal extension for WooCommerce', 'Heads up! Theres a new PayPal on the block!<br /><br />Now is a great time to upgrade to our latest <a href=\"https://woocommerce.com/products/woocommerce-paypal-payments/\" target=\"_blank\">PayPal extension</a> to continue to receive support and updates with PayPal.<br /><br />Get access to a full suite of PayPal payment methods, extensive currency and country coverage, and pay later options with the all-new PayPal extension for WooCommerce.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(15, 'ppxo-pps-upgrade-paypal-payments-2', 'info', 'en_US', 'Upgrade your PayPal experience!', 'Get access to a full suite of PayPal payment methods, extensive currency and country coverage, offer subscription and recurring payments, and the new PayPal pay later options.<br /><br />Start using our <a href=\"https://woocommerce.com/products/woocommerce-paypal-payments/\" target=\"_blank\">latest PayPal today</a> to continue to receive support and updates.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(16, 'woocommerce-core-sqli-july-2021-need-to-update', 'update', 'en_US', 'Action required: Critical vulnerabilities in WooCommerce', 'In response to a critical vulnerability identified on July 13, 2021, we are working with the WordPress Plugins Team to deploy software updates to stores running WooCommerce (versions 3.3 to 5.5) and the WooCommerce Blocks feature plugin (versions 2.5 to 5.5).<br /><br />Our investigation into this vulnerability is ongoing, but <strong>we wanted to let you know now about the importance of updating immediately</strong>.<br /><br />For more information on which actions you should take, as well as answers to FAQs, please urgently review our blog post detailing this issue.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(17, 'woocommerce-blocks-sqli-july-2021-need-to-update', 'update', 'en_US', 'Action required: Critical vulnerabilities in WooCommerce Blocks', 'In response to a critical vulnerability identified on July 13, 2021, we are working with the WordPress Plugins Team to deploy software updates to stores running WooCommerce (versions 3.3 to 5.5) and the WooCommerce Blocks feature plugin (versions 2.5 to 5.5).<br /><br />Our investigation into this vulnerability is ongoing, but <strong>we wanted to let you know now about the importance of updating immediately</strong>.<br /><br />For more information on which actions you should take, as well as answers to FAQs, please urgently review our blog post detailing this issue.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(18, 'woocommerce-core-sqli-july-2021-store-patched', 'update', 'en_US', 'Solved: Critical vulnerabilities patched in WooCommerce', 'In response to a critical vulnerability identified on July 13, 2021, we worked with the WordPress Plugins Team to deploy software updates to stores running WooCommerce (versions 3.3 to 5.5) and the WooCommerce Blocks feature plugin (versions 2.5 to 5.5).<br /><br /><strong>Your store has been updated to the latest secure version(s)</strong>. For more information and answers to FAQs, please review our blog post detailing this issue.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(19, 'woocommerce-blocks-sqli-july-2021-store-patched', 'update', 'en_US', 'Solved: Critical vulnerabilities patched in WooCommerce Blocks', 'In response to a critical vulnerability identified on July 13, 2021, we worked with the WordPress Plugins Team to deploy software updates to stores running WooCommerce (versions 3.3 to 5.5) and the WooCommerce Blocks feature plugin (versions 2.5 to 5.5).<br /><br /><strong>Your store has been updated to the latest secure version(s)</strong>. For more information and answers to FAQs, please review our blog post detailing this issue.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(20, 'habit-moment-survey', 'marketing', 'en_US', 'Were all ears! Share your experience so far with WooCommerce', 'Wed love your input to shape the future of WooCommerce together. Feel free to share any feedback, ideas or suggestions that you have.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(21, 'ecomm-wc-navigation-survey', 'info', 'en_US', 'Wed like your feedback on the WooCommerce navigation', 'Were making improvements to the WooCommerce navigation and would love your feedback. Share your experience in this 2 minute survey.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(22, 'woocommerce-core-paypal-march-2022-updated', 'update', 'en_US', 'Security auto-update of WooCommerce', '<strong>Your store has been updated to the latest secure version of WooCommerce</strong>. We worked with WordPress to deploy PayPal Standard security updates for stores running WooCommerce (version 3.5 to 6.3). Its recommended to disable PayPal Standard, and use <a href=\"https://woocommerce.com/products/woocommerce-paypal-payments/\" target=\"_blank\">PayPal Payments</a> to accept PayPal.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(23, 'woocommerce-core-paypal-march-2022-updated-nopp', 'update', 'en_US', 'Security auto-update of WooCommerce', '<strong>Your store has been updated to the latest secure version of WooCommerce</strong>. We worked with WordPress to deploy security updates related to PayPal Standard payment gateway for stores running WooCommerce (version 3.5 to 6.3).', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(24, 'pinterest_03_2022_update', 'marketing', 'en_US', 'Your Pinterest for WooCommerce plugin is out of date!', 'Update to the latest version of Pinterest for WooCommerce to continue using this plugin and keep your store connected with Pinterest. To update, visit <strong>Plugins &gt; Installed Plugins</strong>, and click on update now under Pinterest for WooCommerce.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(25, 'store_setup_survey_survey_q2_2022', 'survey', 'en_US', 'How is your store setup going?', 'Our goal is to make sure you have all the right tools to start setting up your store in the smoothest way possible.\r\nWed love to know if we hit our mark and how we can improve. To collect your thoughts, we made a 2-minute survey.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(26, 'wc-admin-wisepad3', 'marketing', 'en_US', 'Take your business on the go in Canada with WooCommerce In-Person Payments', 'Quickly create new orders, accept payment in person for orders placed online, and automatically sync your inventory  no matter where your business takes you. With WooCommerce In-Person Payments and the WisePad 3 card reader, you can bring the power of your store anywhere.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(27, 'woocommerce-payments-august-2022-need-to-update', 'update', 'en_US', 'Action required: Please update WooCommerce Payments', 'An updated secure version of WooCommerce Payments is available  please ensure that youre using the latest patch version. For more information on what action you need to take, please review the article below.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(28, 'woocommerce-payments-august-2022-store-patched', 'update', 'en_US', 'WooCommerce Payments has been automatically updated', 'Youre now running the latest secure version of WooCommerce Payments. Weve worked with the WordPress Plugins team to deploy a security update to stores running WooCommerce Payments (version 3.9 to 4.5). For further information, please review the article below.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(29, 'shipping_category_q4_2022', 'marketing', 'en_US', 'Save time on shipping', 'Is your store all set to ship? Save valuable time (and money!) by automating your fulfillment process for the busiest shopping season. Explore our range of trusted shipping partners to get started.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(30, 'woocommerce_admin_deprecation_q4_2022', 'info', 'en_US', 'WooCommerce Admin is part of WooCommerce!', 'To make sure your store continues to run smoothly, check that WooCommerce is up-to-date  at least version 6.5  and then disable the WooCommerce Admin plugin.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(31, 'tiktok-targeted-q4-2022', 'marketing', 'en_US', 'Get $200 in ad credit from TikTok after you spend $20 on your first campaign', 'Reach one billion shoppers with TikTok for WooCommerce this holiday season! Sync your product catalog, capture insights, and create ad campaigns right from your dashboard. Connect your store today to unlock this limited time offer! <a href=\"https://ads.tiktok.com/help/article?aid=10011326\">Terms &amp; conditions apply</a>.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(32, 'paypal_paylater_g3_q4_22', 'marketing', 'en_US', 'Turn browsers into buyers with Pay Later', 'Add PayPal at checkout, plus give customers a buy now, pay later option from the name they trust. With Pay in 4 &amp; Pay Monthly, available in PayPal Payments, you get paid up front while letting customers spread their payments over time. Boost your average order value and convert more sales  at no extra cost to you.', '{}', 'unactioned', 'woocommerce.com', '2022-11-17 23:01:38', NULL, 0, 'plain', '', 0, 0, 'info'),
(33, 'paypal_paylater_g2_q4_22', 'marketing', 'en_US', 'Upgrade to PayPal Payments to offer Pay Later at checkout', 'PayPal Pay Later is included in PayPal Payments at no additional cost to you. Customers can spread their payments over time while you get paid up front. \r\nTheres never been a better time to upgrade your PayPal plugin. Simply download it and connect with a PayPal Business account.', '{}', 'pending', 'woocommerce.com', '2022-11-17 06:43:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(34, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Sambungkan ke WooCommerce.com', 'Sambungkan untuk mendapatkan pemberitahuan dan pembaruan produk penting.', '{}', 'unactioned', 'woocommerce-admin', '2022-11-17 06:43:45', NULL, 0, 'plain', '', 0, 0, 'info'),
(35, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Memilih tema?', 'Periksa tema yang kompatibel dengan WooCommerce dan pilih tema yang sesuai dengan kebutuhan merek dan bisnis Anda.', '{}', 'unactioned', 'woocommerce-admin', '2022-11-18 22:46:41', NULL, 0, 'plain', '', 0, 0, 'info'),
(36, 'wc-admin-insight-first-product-and-payment', 'survey', 'en_US', 'Wawasan', 'Lebih dari 80% penjual baru menambahkan produk pertama dan menyiapkan setidaknya satu metode pembayaran pada minggu pertama.<br><br>Apakah menurut Anda tipe wawasan ini bermanfaat?', '{}', 'unactioned', 'woocommerce-admin', '2022-11-18 22:46:42', NULL, 0, 'plain', '', 0, 0, 'info'),
(37, 'wc-admin-launch-checklist', 'info', 'en_US', 'Siap meluncurkan toko Anda?', 'Untuk memastikan Anda tidak merasa melupakan sesuatu, kami telah menyusun daftar periksa pra-peluncuran yang penting.', '{}', 'unactioned', 'woocommerce-admin', '2022-11-18 22:46:42', NULL, 0, 'plain', '', 0, 0, 'info'),
(38, 'wc-admin-orders-milestone', 'info', 'en_US', 'Pesanan pertama diterima', 'Selamat atas pesanan pertama Anda! Sekarang adalah waktu yang tepat untuk mempelajari cara mengelola pesanan Anda.', '{\"current_milestone\":1}', 'unactioned', 'woocommerce-admin', '2022-11-19 00:44:38', NULL, 0, 'plain', '', 0, 0, 'info'),
(39, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'Cara menyesuaikan katalog produk Anda', 'Anda pasti ingin katalog produk dan gambar terlihat bagus dan sesuai dengan merek Anda. Panduan ini akan memberikan semua tips yang diperlukan agar produk Anda tampil keren di toko.', '{}', 'unactioned', 'woocommerce-admin', '2022-11-22 10:14:42', NULL, 0, 'plain', '', 0, 0, 'info'),
(40, 'wc-admin-mobile-app', 'info', 'en_US', 'Instal aplikasi seluler Woo', 'Instal aplikasi seluler WooCommerce untuk mengelola pesanan, menerima pemberitahuan penjualan, dan melihat metrik kunci  di mana pun Anda berada.', '{}', 'unactioned', 'woocommerce-admin', '2022-11-22 10:14:42', NULL, 0, 'plain', '', 0, 0, 'info');
